mapper.select:
SELECT

FROM `article`

	
		article_status=#{status}
	

ORDER BY article_status ASC, article_order DESC, article_id DESC
limit
#{startPos},#{pageSize}

SELECT COUNT(*) FROM `article`
article.article_tag_ids = #{tagId} OR article.article_tag_ids LIKE "${tagId},%" OR article.article_tag_ids LIKE "%,${tagId},%" OR article.article_tag_ids LIKE "%,${tagId}"
AND article_status=#{status}

SELECT
FROM `article`
article_status=#{status} AND
article_parent_category_id=#{parentCategoryId} OR
article_child_category_id=#{childCategoryId}
ORDER BY article_status ASC, article_order DESC, article_id DESC limit #{limit}

SELECT

FROM
`article`

	
		article_status=#{status}
	

ORDER BY article_view_count DESC,article_order DESC, article_id DESC
limit #{limit}

SELECT
FROM `article`
article_id > #{articleId}
AND article_status=#{status}
ORDER BY article_id limit 1

SELECT
FROM `article`
article_id < #{articleId}
AND article_status=#{status}
ORDER BY article_id limit 1

SELECT
FROM `article`
article_status=#{status}
ORDER BY RAND() limit #{num}

SELECT
FROM `article`
article_status=#{status}
ORDER BY article_comment_count DESC,article_order DESC, article_id DESC limit #{num}
